import React, { useState, useEffect } from "react";

// index import
import man from "../assests/man.png";
import background_img from "../assests/background.jpg";

function Navbar() {
    // State variables for background color and padding top
    const [bgColor, setBgColor] = useState("transparent");
    const [paddingTop, setPaddingTop] = useState("20px"); // Initial padding top
    const [TextColor, setTextColor] = useState("white");
    const [TextHoverColor, setTextHoverColor] = useState("black")

    // useEffect to handle scroll event and update background color and padding top
    useEffect(() => {
        const handleScroll = () => {
            // Change background color and padding top based on scroll position
            if (window.scrollY > 100) {
                setBgColor("#d1d5db");
                setPaddingTop("0px"); // Change padding top
                setTextColor("black");
                setTextHoverColor("black")
            } else {
                setBgColor("transparent");
                setPaddingTop("2vh"); // Reset padding top
                setTextColor("#d1d5db")
            }
        };

        // Add event listener when component mounts
        window.addEventListener("scroll", handleScroll);

        // Remove event listener when component unmounts
        return () => {
            window.removeEventListener("scroll", handleScroll);
        };
    }, []);

    // useEffect to save background color and padding top to localStorage
    useEffect(() => {
        localStorage.setItem("bgColor", bgColor);
        localStorage.setItem("paddingTop", paddingTop);
    }, [bgColor, paddingTop]);


    return (
        <>
            <nav class="fixed  md:py-4 w-[-webkit-fill-available] " style={{
                backgroundColor: bgColor,
                paddingTop: paddingTop,
                transition: "background-color 0.3s, padding-top 0.3s",
            }} >
                <div className="flex flex-col  sm:flex-row sm:mx-10 lg:mx-40 text-center justify-between" style={{color: TextColor, pointer:'cursor'}} >
                    <a
                        href=""
                        className="baloo-paaji py-4 text-3xl font-medium"
                    >
                        MohitJi
                    </a>
                    <div className=" flex text-center px-2 mb-5  bg-[#ffffff2e]  rounded-3xl  py-1.5 sm:align-middle sm:m-auto font-normal  text-gray-700 justify-center text-sm  ">
                        <a
                            href=""
                            class="mx-1.5 text-white rounded-2xl px-3 sm:px-5 py-1.5 hover:bg-gray-100 hover:text-black"
                        >
                            HOME
                        </a>
                        <a
                            href=""
                            class="mx-1.5 text-white rounded-2xl px-3 sm:px-5 py-1.5 hover:bg-gray-100 hover:text-black"
                        >
                            PROJECTS
                        </a>
                        <a
                            href=""
                            class="mx-1.5 text-white rounded-2xl px-3 sm:px-5 py-1.5 hover:bg-gray-100 hover:text-black"
                        >
                            RESUME
                        </a>
                        <a
                            href=""
                            class="mx-1.5 text-white rounded-2xl px-3 sm:px-5 py-1.5 hover:bg-gray-100 hover:text-black"
                        >
                            ABOUT ME
                        </a>
                    </div>
                    <div className="flex">
                        <button className="hidden sm:block text-sm  text-white border-[#ffffff7c] hover:bg-white hover:text-black border px-5 py-1.5 rounded m-auto ">
                            CONTACT ME
                        </button>
                    </div>
                </div>
            </nav>

            <main className="min-h-[150vh] ">
                <section className=" min-h-[100vh] IndexHeaderBackground flex">
                    <div className="m-auto text-center">
                        <h2 className="text-gray-400 mb-3 text-5xl">HI, I AM MOHIT</h2>
                        <div className="">
                            <h3 className="text-gray-300 text-[7rem] font-bold leading-[5.8rem] font-mono francois-one-regular">Full Stack Web </h3>
                            <h3 className="text-gray-300 text-[7rem] font-bold leading-[5.8rem] font-mono francois-one-regular">Developer</h3>
                        </div>
                        <p className="text-gray-400 text-lg py-8">Let's turn your visionary concepts into digital wonders,  crafting each <br /> element with expertise and care.</p>
                        <button className="text-white  outline outline-offset-8 outline-white rounded-3xl hover:bg-gray-300 hover:text-black bg-[#ffffff2e] px-20 py-2 "> Let's Talk</button>
                    </div>
                </section>
            </main>
        </>
    );
}
export default Navbar;
