import React from "react";
import Navbar from "../Components/Navbar";
import { Link } from "react-router-dom";

// icoons import
import InstaIcon from "../assests/icons/insta.png";
import LinkedinIcon from "../assests/icons/linkedin.png";
import WhatsappIcon from "../assests/icons/whatsapp.png";
import GmailIcon from "../assests/icons/gmail.png";

export default function ResumePage({ ProjectsDatabase }) {
  //    function to download resume
  function downloadResume() {
    // Path of resume file
    const resumeFilePath = "./Mohit Sharma.pdf";

    // Create a temporary anchor element
    const anchor = document.createElement("a");

    // Set the href attribute to the path of the resume file
    anchor.href = resumeFilePath;

    // Append the anchor to the body
    document.body.appendChild(anchor);

    // Click the anchor element to initiate download
    anchor.click();

    // Remove the anchor element from the body
    document.body.removeChild(anchor);
  }
  return (
    <>
      <div className="bg-black fixed  block h-36 w-full md:h-20">
        <Navbar />
      </div>
      <section className="flex flex-col ">
        <div className="IndexHeaderBackground h-80 flex pt-20 w-full">
          <h3 className="text-gray-300 md:text-5xl text-center m-auto text-6xl font-bold md:leading-[5.8rem] font-mono francois-one-regular">
            PROJECTS
          </h3>
        </div>
        <div className="flex">
          <div className="m-auto">
            {ProjectsDatabase.map((project, index) => (
              <Link
                to={`/project/${project.ProjectId}`}
                className="w-full flex  m-auto  p-4"
                onClick={() => PortfolioPage(project.ProjectId)}
                key={index}
              >
                <div className="flex w-full  rounded md:border-0 m-auto border-gray-200 border p-1 hover:shadow-2xl  ">
                  <img
                    alt="project"
                    className="block inset-0  w-[60vh]  md:h-52 object-cover object-center"
                    src={`/ProjectImages/${project.img1}`}
                  />
                  <div className="p-2 z-10   bg-[#00000093] opacity-100 ">
                    <div className="flex h-full w-full">
                      <h1 className="title-font lg:text-lg m-auto font-medium text-center text-white">
                        {project.ProjectName}
                      </h1>
                    </div>
                  </div>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </section>

      {/* My social section  */}
