import React, { useEffect, useState } from "react";
import Navbar from "../Components/Navbar";

export default function ProjectPage({ selectedproject, ProjectsDatabase }) {
  const [ProjectName, setProjectName] = useState(null);
  const [ProjectDescription, setProjectDescription] = useState(null);
  const [ProjectSkills, setProjectSkills] = useState(null);
  const [ProjectGitLink, setProjectGitLink] = useState(null);
  const [ProjectLiveLink, setProjectLiveLink] = useState(null);
  const [ProjectImg1, setProjectImg1] = useState(null)
  const [ProjectImg2, setProjectImg2] = useState(null)
  const [ProjectImg3, setProjectImg3] = useState(null)

  useEffect(() => {
    console.log("selectedproject", selectedproject);
    // Find the selected project in ProjectsDatabase
    const foundProject = ProjectsDatabase.find(
      (project) => project.ProjectId === selectedproject
    );
    if (foundProject) {
      setProjectName(foundProject.ProjectName);
      setProjectSkills(foundProject.ProjectSkills);
      setProjectDescription(foundProject.ProjectDescription);
      setProjectGitLink(foundProject.GitLink);
      setProjectLiveLink(foundProject.LiveLink);
    }
  }, [selectedproject, ProjectsDatabase]);

  return (
    <>
      <div className="bg-black fixed  block h-36 w-full md:h-20">
        <Navbar />
      </div>
      <main>
        <section className="flex flex-col pt-40">
          <div className="flex flex-col p-4 gap-5">
            <img
              className="m-auto "
              src={`./ProjectImages/${ProjectImg1}`}
              alt="img"
            />
            <img
              className="m-auto "
              src={`./ProjectImages/${ProjectImg2}`}
              alt=""
            />
            <img
              className="m-auto "
              src={`./ProjectImages/${ProjectImg3}`}
              alt=""
            />
          </div>

          <div className="flex flex-col p-4  ">
            <div className="m-auto text-lg flex flex-col gap-5">

              <h1 className="text-center text-2xl font-bold">{ProjectName}</h1>

              
              <span className=""> <span className="font-bold ">Skills : </span>  {ProjectSkills} </span>

              <p className="text-justify"><span className="font-bold ">Description : </span> {ProjectDescription}</p>

              <div className="flex flex-col w-60 m-auto gap-5">
              <a href={ProjectGitLink} target="_blank" className="text-2xl border-2  py-1 rounded-lg font-mono text-center">
                {" "}
                Git Link
              </a>
              <a href={ProjectLiveLink} target="_blank" className="text-2xl border-2 py-1 rounded-lg font-mono text-center">
                {" "}
                Live Preview{" "}
              </a>
              </div>
            </div>
          </div>

          {/* <div className="m-auto text-center">
            <h3 className="text-gray-300 md:text-5xl text-3xl font-bold md:leading-[5.8rem] font-mono francois-one-regular">
              Full Stack Web{" "}
            </h3>
          </div> */}
        </section>
      </main>
    </>
  );
}
